import React from 'react';
import { Link, useLocation } from 'react-router-dom';


const Navbar = (props) => {
	const handleLogOut = () => {
		props.showAlert('Logged out.', 'info');
		localStorage.removeItem('token');
	}

	return (
		<>
			<nav className="navbar navbar-expand-lg navbar-dark bg-dark">
				<div className="container-fluid">
					<Link className="navbar-brand" to="/">yourNotebook</Link>
					<button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="/navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
						<span className="navbar-toggler-icon"></span>
					</button>
					<div className="collapse navbar-collapse" id="navbarSupportedContent">
						<ul className="navbar-nav me-auto mb-2 mb-lg-0">
							<li className="nav-item">
								<Link className={`nav-link ${useLocation().pathname === '/'?'active':''}`} to="/">Home</Link>
							</li>
							<li className="nav-item">
								<Link className={`nav-link ${useLocation().pathname === '/about'?'active':''}`} to="/about">About</Link>
							</li>
						</ul>
						{!localStorage.getItem('token') ? <form className="d-flex">
							<Link to="/login" className="btn btn-outline-info btn-sm mx-1 " role='button'>Log In</Link>
							<Link to="/signup" className="btn btn-outline-info btn-sm mx-1 " role='button'>Sign Up</Link>
						</form> : <Link to="/login" className="btn btn-outline-info btn-sm mx-1 " role='button' onClick={handleLogOut}>Log Out</Link>}
					</div>
				</div>
			</nav>
		</>
	)
}

export default Navbar
